mapper.update:
update crm.cacc a set 
	balance = balance + #{quantity} ,
	freemargin = freemargin + #{quantity} , 
	changebalan = changebalan + #{quantity} 
where 
	a.PNSGID = #{pnsgid} and a.pnsid = #{pnsid} and a.cid = #{cid}

update crm.cacc set 
	balance = balance - #{quantity} ,
	freemargin = freemargin - #{quantity} , 
	changebalan = changebalan - #{quantity} 
where 
	pnsgid = #{pnsgid} and pnsid = #{pnsid} and cid = #{cid}
